using System;
using System.Collections.Generic;
using System.Linq;
using System.Web;
using System.Web.Mvc;
using KABE_Food_Ordering_System.Logic;
using KABE_Food_Ordering_System.Models;

namespace KABE_Food_Ordering_System.Controllers
{
    public class RestaurantController : Controller
    {
        RestaurantLogic restaurantLogic = new RestaurantLogic();
        public BaseLogic<User> userLogic = new BaseLogic<User>(new ApplicationDbContext());
        public BaseLogic<Restaurant> baseLogic = new BaseLogic<Restaurant>(new ApplicationDbContext());

        // GET: Restaurant
        [AdminRoleRestrictLogic]
        public ActionResult Index()
        {
            return View(baseLogic.GetAll());
        }

        //GET: User/Create
        [RestaurantRestrictLogic]
        public ActionResult Register()
        {
            string status = Convert.ToString(Session["Status"]);
            string setStatus = Convert.ToString(Status.ProfileNotCreated);
            ApplicationDbContext _context = new ApplicationDbContext();

            if (status == setStatus)
            {
                Restaurant restaurant = restaurantLogic.GetRestaurantByEmail(Convert.ToString(Session["Email"]));
                if (restaurant == null)
                {
                    ViewBag.Message = "Error";
                }

                var model = new Restaurant()
                {
                    Locations = _context.Locations.ToList(),
                    Id = restaurant.Id

                };
                return View(model);
            }
            else
            {
                return View("Update", "Restaurant");
            }


        }

        //POST: User/Create
        [RestaurantRestrictLogic]
        [HttpPost]
        [ValidateAntiForgeryToken]
        public ActionResult Register([Bind(Include = "Id,About,Email,Name,LocationId,ResidentialAddress,PhoneNumber,EstablishmentDate"/*,Restaurants"*/)] Restaurant model)
        {
            string Email = Convert.ToString(Session["Email"]);
            Restaurant restLogic = restaurantLogic.GetRestaurantByEmail(Email);
            if (restLogic != null)
            {
                restLogic.SmallThumbnailjpg = "";

                restLogic.SmallThumbnailpng = "";
                restLogic.About = model.About;
                restLogic.PhoneNumber = model.PhoneNumber;
                var date = Convert.ToDateTime(model.EstablishmentDate);
                restLogic.EstablishmentDate = date;
                restLogic.ResidentialAddress = model.ResidentialAddress;
                baseLogic.Update(restLogic);
                User user = userLogic.Get(restLogic.RestaurantsId);
                user.Status = Status.ChangePassword;
                //user.SecretQuestions = model.Restaurants.SecretQuestions;
                userLogic.Update(user);

                ViewBag.Message = "Success";

                //return RedirectToAction("Details", "Restaurant");
                return RedirectToAction("ChangePassword", "User");

            }
            else
            {
                ViewBag.Message = "Exist";
                return View(model);
            }

        }

        //GET: User/Create
        [HttpGet]
        [RestaurantRestrictLogic]
        public ActionResult Update()
        {
            int Id = Convert.ToInt32(Session["id"]);

            ApplicationDbContext _context = new ApplicationDbContext();


            Restaurant restaurant = restaurantLogic.GetByUserID(Id);
            restaurant.Locations = _context.Locations.ToList();
            if (restaurant == null)
            {
                ViewBag.Message = "Error";
                return View();
            }


            return View(restaurant);



        }

        //POST: User/Create
        [RestaurantRestrictLogic]
        [HttpPost]
        [ValidateAntiForgeryToken]
        public ActionResult Update([Bind(Include = "Id,About,Email,Name,LocationId,ResidentialAddress,PhoneNumber,EstablishmentDate"/*,Restaurants"*/)] Restaurant model)
        {
            ApplicationDbContext _context = new ApplicationDbContext();
            model.Locations = _context.Locations.ToList();
            string Email = Convert.ToString(Session["Email"]);
            Restaurant restLogic = restaurantLogic.GetRestaurantByEmail(Email);
            if (restLogic != null)
            {
                restLogic.About = model.About;
                restLogic.PhoneNumber = model.PhoneNumber;
                var date = Convert.ToDateTime(model.EstablishmentDate);
                restLogic.EstablishmentDate = date;
                restLogic.ResidentialAddress = model.ResidentialAddress;
                restLogic.LocationId = model.LocationId;
                baseLogic.Update(restLogic);

                ViewBag.Message = "Success";

                return RedirectToAction("ViewProfile", "Restaurant");

            }
            else
            {
                ViewBag.Message = "Exist";
                return View(model);
            }

        }


        //GET: Restaurant/Details/{id}
        [RestaurantRestrictLogic]
        public ActionResult Details(int? id)
        {
            if (id == null)
            {
                return HttpNotFound();
            }
            Restaurant restaurant = baseLogic.Get(id);
            if (restaurant == null)
            {
                return HttpNotFound();
            }
            return View(restaurant);
        }

        //GET: Restaurant/ViewProfile/{id}
        [RestaurantRestrictLogic]
        public ActionResult ViewProfile()
        {
            int id = Convert.ToInt32(Session["id"]);
            if (id == 0)
            {
                return HttpNotFound();
            }
            Restaurant restaurant = restaurantLogic.GetByUserID(id);

            if (restaurant == null)
            {
                return HttpNotFound();
            }
            return View(restaurant);
        }

    }
}